select.execute:
		  var data,
			  uidsStr,
              userStatus,			  
			  prefixed = [],
			  ids = uids.split(';'),
			  len = ids.length;

		  for(var i=0; i<len; i++){
			var id = ids[i];
			prefixed[i] = id ? (siteid + id) : '';
		  }
		  uidsStr = prefixed.join(';');
		  if(!uidsStr){
			uids = userStatus = null;
		  }else{
			data = request.query('uids',uidsStr).get().response;
			var statusArr = data.match(/=\d/g);
			for(var i=0,len=statusArr.length;i<len;i++){
				var user = <{uids[i]} />;
				user.@['value'] = statusArr[i].split('=')[1];
			    userStatus += user;	
			}
		  }

          response.object = <info>
								<request_url>{request.url}</request_url>
								<uids>{uids}</uids>
								<status>{userStatus}</status>
							</info>
